import React, { useState } from 'react';
import { PieChart } from '@mui/x-charts/PieChart';

function BatteryMaterialAndCompositionForm() {
  const [showBatteryCompoForm, setShowBatteryCompoForm] = useState(false);
  const [showBatteryRawForm, setShowBatteryRawForm] = useState(false);

  const toggleBatteryCompoForm = () => {
    setShowBatteryCompoForm(!showBatteryCompoForm);
};
const toggleBatteryRawForm = () => {
  setShowBatteryCompoForm(!showBatteryCompoForm);
};
  // // Définir les états pour les champs du formulaire
  // const [name, setName] = useState('');
  // const [email, setEmail] = useState('');
  // const [password, setPassword] = useState('');
  // const [confirmPassword, setConfirmPassword] = useState('');

  // // Gérer la soumission du formulaire
  // const handleSubmit = (event) => {
  //   event.preventDefault();
  //   // Envoyer les données du formulaire
  //   console.log('Name:', name);
  //   console.log('Email:', email);
  //   console.log('Password:', password);
  //   console.log('Confirm Password:', confirmPassword);
  //   // Réinitialiser les champs du formulaire après soumission
  //   setName('');
  //   setEmail('');
  //   setPassword('');
  //   setConfirmPassword('');
  // };

  return (
    <div className='flex-col justify-center p-10'>
      <div className='flex mx-auto justify-between w-[50%]'>
        <h2 className="text-2xl font-bold">Design of Circularity</h2>
        <button onClick={toggleBatteryCompoForm} className='w-fit'>
            {showBatteryCompoForm ? (
                <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
            ) : (
                <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
                </svg>
            )}
        </button>
      </div>
      <div className='flex'>
      {showBatteryCompoForm && (
        <PieChart
          series={[
            {
              data: [
                { id: 0, value: 10, label: 'series A' },
                { id: 1, value: 15, label: 'series B' },
                { id: 2, value: 2, label: 'series C' },
                { id: 3, value: 5, label: 'series D' },
                { id: 4, value: 7, label: 'series E' },
                { id: 5, value: 8, label: 'series F' },
                { id: 6, value: 5, label: 'series G' },
                { id: 7, value: 10, label: 'series H' },
              ],
            },
          ]}
          width={1000}
          height={500}
        />
      )}
      </div>
      
    </div>
  );
}

export default BatteryMaterialAndCompositionForm;
